Scripts.OnEnter:
if ( self.tooltipText ) then
GameTooltip:SetOwner(self, self.tooltipOwnerPoint or "ANCHOR_RIGHT");
GameTooltip:SetText(self.tooltipText, nil, nil, nil, nil, true);
end
if ( self.tooltipRequirement ) then
GameTooltip:AddLine(self.tooltipRequirement, 1.0, 1.0, 1.0, true);
GameTooltip:Show();
end

if ( self.tooltipText ) then
GameTooltip:SetOwner(self, self.tooltipOwnerPoint or "ANCHOR_RIGHT");
GameTooltip:SetText(self.tooltipText, nil, nil, nil, nil, true);
end
if ( self.tooltipRequirement ) then
GameTooltip:AddLine(self.tooltipRequirement, 1.0, 1.0, 1.0, true);
GameTooltip:Show();
end

if (self.tooltip) then
  GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
  GameTooltip:SetText(self.tooltip, nil, nil, nil, nil, (self.tooltipStyle or true));
end
self:SetAlpha(1)

if ( not self.isDisabled ) then
GameTooltip:SetOwner(self, "ANCHOR_TOPRIGHT");
GameTooltip:SetText(self.tooltip, nil, nil, nil, nil, true);
end

if ( not self.isDisabled ) then
GameTooltip:SetOwner(self, "ANCHOR_TOPRIGHT");
GameTooltip:SetText(self.tooltip, nil, nil, nil, nil, true);
end

Scripts.OnLoad:
self.tooltipText = Tinom.L["屏蔽此时间内的重复消息\n0为不限时间."];
self:SetMinMaxValues(0,300);
self:SetValueStep(1);
self:SetValue(0);
self:SetOrientation("HORIZONTAL");
_G[self:GetName().."Low"]:SetText("")
_G[self:GetName().."High"]:SetText("")

Scripts.OnValueChanged:
value = Tinom.SliderValueFloor(value);
self.value = value;
self:SetValue(value);
self.Text:SetText(self.value.."秒");

Scripts.OnLoad:
self.tooltipText = Tinom.L["屏蔽此时间内的任何消息\n0为不限时间."];
self:SetMinMaxValues(0,300);
self:SetValueStep(1);
self:SetValue(0);
self:SetOrientation("HORIZONTAL");
_G[self:GetName().."Low"]:SetText("")
_G[self:GetName().."High"]:SetText("")

Scripts.OnValueChanged:
value = Tinom.SliderValueFloor(value);
self.value = value;
self:SetValue(value);
self.Text:SetText(self.value.."秒");

Scripts.OnLoad:
Tinom.FilterSettingFrames_SetTitle();
local FrameName = self:GetName();
local lastButton = _G[FrameName.."FilterButton1"]
for i=2,14 do
  local Button2 = CreateFrame("Frame",FrameName.."FilterButton"..i,self,"TinomFilterButtonTemplate",i)
  Button2:SetPoint("TOPLEFT", lastButton ,"BOTTOMLEFT", 0, 0)
  lastButton = Button2;
end
