Scripts.OnLoad:
self.tooltipText = Tinom.L["屏蔽此时间内的重复消息\n0为不限时间."];
self:SetMinMaxValues(0,300);
self:SetValueStep(1);
self:SetValue(0);
self:SetOrientation("HORIZONTAL");
_G[self:GetName().."Low"]:SetText("")
_G[self:GetName().."High"]:SetText("")

Scripts.OnValueChanged:
value = math.floor(value * 100 + 0.5) / 100;
if (value ~= 0) then
  value = math.max(0.5, value);
end
self.value = value;
self:SetValue(value);
TinomOptionsMainPanelBaseSettingSlider_RepeatMsgText:SetText(self.value.."秒");

Scripts.OnLoad:
self.tooltipText = Tinom.L["屏蔽此时间内的任何消息\n0为不限时间."];
self:SetMinMaxValues(0,300);
self:SetValueStep(1);
self:SetValue(0);
self:SetOrientation("HORIZONTAL");
_G[self:GetName().."Low"]:SetText("")
_G[self:GetName().."High"]:SetText("")

Scripts.OnValueChanged:
value = math.floor(value * 100 + 0.5) / 100;
if (value ~= 0) then
  value = math.max(0.5, value);
end
self.value = value;
self:SetValue(value);
TinomOptionsMainPanelBaseSettingSlider_IntervalMsgText:SetText(self.value.."秒");

Scripts.OnLoad:
_G[self:GetName().."Text2"]:SetText(Tinom.L["声音提醒需要开启\n相应的白名单过滤"]);
self.type = CONTROLTYPE_DROPDOWN;
BlizzardOptionsPanel_RegisterControl(self, self:GetParent());

Scripts.OnEnter:
if ( not self.isDisabled ) then
GameTooltip:SetOwner(self, "ANCHOR_TOPRIGHT");
GameTooltip:SetText(self.tooltip, nil, nil, nil, nil, true);
end

Scripts.OnEditFocusGained:
if (self:GetText() == Tinom.L["角色名"]) then
  self:SetText("");
end

if (self:GetText() == Tinom.L["新角色名-可选"]) then
  self:SetText("");
end

if (self:GetText() == Tinom.L["新消息-可选"]) then
  self:SetText("");
end

if (self:GetText() == Tinom.L["关键字"]) then
  self:SetText("");
end

if (self:GetText() == Tinom.L["新关键字-可选"]) then
  self:SetText("");
end

if (self:GetText() == Tinom.L["新消息-可选"]) then
  self:SetText("");
end

Scripts.OnEnter:
if ( not self.isDisabled ) then
  GameTooltip:SetOwner(self, "ANCHOR_TOPRIGHT");
  GameTooltip:SetText(self.tooltip, nil, nil, nil, nil, true);
end
